var telepath = require('../index.js')

module.exports = function (id) {
  telepath(this) // make this module telepathic!!!
  var self = this

  setInterval(function () {
    var time = new Date()
    self.send(JSON.stringify(time.getTime()))
  },200)

  this.recv = function (data) {
    console.dir(id+' has gotten: '+data.toString())
  }
}
